class Noticia {  final String title;  final String img;  Noticia({this.title, this.img});  static List<Noticia> fromJsonList(List<dynamic> json) {    List<Noticia> list = List<Noticia>();    for (var value in json) {      list.add(Noticia(          title: value["title"]["rendered"],          img: value["_embedded"]["wp:featuredmedia"][0]["source_url"]));    }    return list;  }}