import 'package:flutter/material.dart';import 'package:comunicacion_publica/Model/Noticia.dart';class NewHeader extends StatelessWidget {  final Noticia noticia;  const NewHeader({Key key, this.noticia}) : super(key: key);  @override  Widget build(BuildContext context) {    return Card(      child: Column(        children: <Widget>[          Image.network(            noticia.img,            width: MediaQuery.of(context).size.width,            fit: BoxFit.cover,            height: 250,          ),          Padding(            padding: EdgeInsets.all(16.0),            child: Text(              noticia.title,            ),          )        ],      ),    );  }}