import 'package:comunicacion_publica/Model/Noticia.dart';import 'package:flutter/material.dart';import 'package:comunicacion_publica/Screens/NewDetail.dart';class NewItem extends StatelessWidget {  final Noticia newS;  NewItem({this.newS});  @override  Widget build(BuildContext context) {    return Material(      color: Colors.transparent,      child: Container(        child: Card(          elevation: 10,          clipBehavior: Clip.hardEdge,          child: InkWell(            onTap: () => navigateToNewDetail(context),            child: Column(              children: <Widget>[                Image.network(                  newS.img,                  fit: BoxFit.cover,                  height: 100,                  width: 200,                ),                SizedBox(                  child: Text(newS.title),                ),              ],            ),          ),        ),      ),    );  }  void navigateToNewDetail(BuildContext context) {    Navigator.of(context).push(MaterialPageRoute<Null>(      builder: (BuildContext context) {        return NewDetail(noticia: newS);      },    ));  }}